var emailTemplate = '<html>'+
'<body>'+
	'<table>'+
		'<tr>'+
			'<td colspan="2"><b>Error occured during the Camunda Nifi Pipeline. Please find the details below</b></td>'+
		'</tr>'+
		'<tr>'+
			'<td width="20%"><b>Process ID: </b></td><td>#pid</td>'+
		'</tr>'+
		'<tr>'+
			'<td width="20%"><b>Table: </b></td><td>#tablename</td>'+
		'</tr>'+
		'<tr>'+
			'<td width="20%"><b>Error Overview: </b></td><td>#overview</td>'+
		'</tr>'+
        '<tr>'+
			'<td width="20%"><b>Nifi Processor: </b></td><td>#processor</td>'+
		'</tr>'+
        '<tr>'+
			'<td width="20%"><b>Error Description: </b></td><td>#flow_description</td>'+
		'</tr>'+
        '<tr>'+
			'<td width="20%"><b>Possible Actions: </b></td><td>#possible_actions</td>'+
		'</tr>'+
        '<tr>'+
			'<td width="20%"><b>Technical Error: </b></td><td>#error</td>'+
		'</tr>'+
	'</table>'+
'</body>'+
'</html>';

var flowFileList = session.get(maxFlowFetchCount);
if (!flowFileList.isEmpty()) {
    flowFileList.forEach(function (flowFile) {
        if (flowFile !== null) {
            try {
                var tablename = flowFile.getAttribute('tablename');
                var pid = flowFile.getAttribute('pid');
                var servicebc_nifi_failure = JSON.parse(flowFile.getAttribute('servicebc_nifi_failure'));
                emailTemplate = emailTemplate.replace('#tablename',tablename)
                                             .replace('#pid',pid)
                                             .replace('#overview',servicebc_nifi_failure['overview'])
                                             .replace('#processor',servicebc_nifi_failure['processor'])
                                             .replace('#flow_description',servicebc_nifi_failure['flow_description'])
                                             .replace('#possible_actions',servicebc_nifi_failure['possible_actions'])
                                             .replace('#error',servicebc_nifi_failure['error']);
                flowFile = session.write(flowFile,
                    new OutputStreamCallback(function (outputStream) {
                        outputStream.write(emailTemplate.getBytes(StandardCharsets.UTF_8))
                    }));
                session.transfer(flowFile, REL_SUCCESS)
            } catch (error) {
                session.transfer(flowFile, REL_FAILURE)
            }

        }
    });
}

