var IOUtils = Java.type("org.apache.commons.io.IOUtils");
var StreamCallback = Java.type("org.apache.nifi.processor.io.StreamCallback");
var StandardCharsets = Java.type("java.nio.charset.StandardCharsets");

flowFile = session.get();
if (flowFile !== null) {
    try {
        // var inputStream = session.read(flowFile);
        // var text = IOUtils.toString(inputStream, StandardCharsets.UTF_8);
        // var data = JSON.parse(text);
        // inputStream.close();
        var pid;
        var postgresData;
        flowFile = session.write(flowFile,
            new StreamCallback(function (inputStream, outputStream) {
                var text = IOUtils.toString(inputStream, StandardCharsets.UTF_8);
                postgresData = text;
                var data = JSON.parse(text);
                pid = data['pid'];
                outputStream.write(JSON.stringify(data).getBytes(StandardCharsets.UTF_8))
            }));

        flowFile = session.putAttribute(flowFile, 'pid', pid);
        flowFile = session.putAttribute(flowFile, 'postgres_data', postgresData);
        session.transfer(flowFile, REL_SUCCESS);
    } catch (error) {
        var nifi_error = {
            "processor": "ExecuteScript",
            "overview": "Unable to set pid attribute in session",
            "flow_description": "At this stage, the data from postgres is converted to json but unable to proceed further because pid cannot be set as a session attribute (needed for later use).",
            "possible_actions": "Trigger the debug workflow (QueryDatabaseTable) by adding the pid and table name and observe the flow.",
            "error": error, 
        };
        flowFile = session.putAttribute(flowFile, 'servicebc_nifi_failure', JSON.stringify(nifi_error));
        session.transfer(flowFile, REL_FAILURE);
    }
}

