var IOUtils = Java.type("org.apache.commons.io.IOUtils");
var StreamCallback = Java.type("org.apache.nifi.processor.io.StreamCallback");
var StandardCharsets = Java.type("java.nio.charset.StandardCharsets");

var flowFile = session.get();
if (flowFile !== null) {
    try {
        // var inputStream = session.read(flowFile);
        // var text = IOUtils.toString(inputStream, StandardCharsets.UTF_8);
        // var data = JSON.parse(text);
        // inputStream.close();
        // var pid;
        flowFile = session.write(flowFile,
            new StreamCallback(function (inputStream, outputStream) {
                var pid = flowFile.getAttribute('pid');
                var tablename = flowFile.getAttribute('tablename');
                var sqlQuery = 'delete from ' + tablename + ' where pid = \'' + pid + '\'';
                var obj = {
                    sqlQuery: sqlQuery
                };
                outputStream.write(JSON.stringify(obj).getBytes(StandardCharsets.UTF_8))
            }));

        flowFile = session.putAttribute(flowFile, 'statement.type', 'SQL');
        session.transfer(flowFile, REL_SUCCESS);
    } catch (error) {
        var nifi_error = {
            "processor": "ExecuteScript",
            "overview": "Unable to generate delete query to delete from postgres",
            "flow_description": "At this stage, the postgres data has been written to analytics but a delete query could not be generated.",
            "possible_actions": "Manually delete from the postgres table by pid.",
            "error": error, 
        }
        flowFile = session.putAttribute(flowFile, 'servicebc_nifi_failure', JSON.stringify(nifi_error));
        session.transfer(flowFile, REL_FAILURE);
    }
}

